exports.Stack = class{
    // stack constructor
    constructor(seed=[]){
        if(Array.isArray(seed)){
            this._stack = seed;
        }
        else{
            this._stack = [];
        }
    };

    //methods

    //1. empty
    empty(){
        return this._stack.length==0;
    };

    //2. size
    size(){
        return this._stack.length;
    }


    //3. push
    push(value){
        this._stack.push(value);
    };

    //4. pop
    pop(){
        if(this.empty())
            return false;
        this._stack.pop();
        return true;
    }

    //5. top
    top(){
        return { empty:this.empty(),value:this._stack[this._stack.length-1]};
    }

};